'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { usePuzzleSession } from '@/hooks/usePuzzleSession';
import { generateSudoku } from '@/lib/puzzle/sudoku/generator';
import { SudokuState, SudokuCell } from '@/lib/puzzle/sudoku/types';
import { SudokuGrid } from './SudokuGrid';
import { NumberPad } from './NumberPad';
import { Button } from '@/components/ui/Button';

interface SudokuEngineProps {
    difficulty: 'easy' | 'medium' | 'hard';
    gridSize: 4 | 6;
}

export const SudokuEngine: React.FC<SudokuEngineProps> = ({ difficulty, gridSize }) => {
    const { activeSession, startSession, completeSession, abandonSession, logEvent } = usePuzzleSession('sudoku');

    const [selectedCell, setSelectedCell] = useState<{ r: number, c: number } | null>(null);
    const [isNotesMode, setIsNotesMode] = useState(false);

    // Undo stack keeping historical versions of the grid
    const [history, setHistory] = useState<SudokuCell[][][]>([]);

    // Safe accessor to our structured state injected into the session
    const state = activeSession?.state as unknown as SudokuState | undefined;

    // Boot up a new session only if we are idle and missing an active session context
    useEffect(() => {
        if (!activeSession && !selectedCell) {
            const blanks = difficulty === 'easy' ? 0.3 : difficulty === 'medium' ? 0.5 : 0.7;
            const initialGrid = generateSudoku(gridSize, blanks);

            const initialState: SudokuState = {
                gridSize,
                grid: initialGrid,
                mistakes: 0,
                timeElapsed: 0,
            };

            startSession(initialState as any);
        }
    }, [activeSession, difficulty, gridSize, startSession, selectedCell]);

    const handleCellClick = useCallback((row: number, col: number) => {
        if (!state) return;
        const targetCell = state.grid[row][col];
        if (targetCell.isGiven || targetCell.value === targetCell.solution) return; // Prevent editing correct/given cells
        setSelectedCell({ r: row, c: col });
    }, [state]);

    const pushHistory = useCallback((currentGrid: SudokuCell[][]) => {
        setHistory(prev => [...prev, currentGrid.map(row => row.map(cell => ({ ...cell, notes: [...cell.notes] })))]);
    }, []);

    const handleUndo = useCallback(() => {
        if (!state || history.length === 0) return;
        const newHistory = [...history];
        const prevGrid = newHistory.pop()!;
        setHistory(newHistory);
        state.grid = prevGrid;
        setSelectedCell(null); // Force react redraw by referencing
    }, [state, history]);

    const handleHint = useCallback(() => {
        if (!state) return;
        // Find empty cell
        let emptyCells: { r: number, c: number }[] = [];
        for (let r = 0; r < gridSize; r++) {
            for (let c = 0; c < gridSize; c++) {
                if (state.grid[r][c].value === null) emptyCells.push({ r, c });
            }
        }
        if (emptyCells.length === 0) return;
        const target = emptyCells[Math.floor(Math.random() * emptyCells.length)];

        pushHistory(state.grid);

        const newGrid = state.grid.map(row => [...row]);
        const cell = { ...newGrid[target.r][target.c], value: newGrid[target.r][target.c].solution, isError: false, notes: [] };
        newGrid[target.r][target.c] = cell;

        state.grid = newGrid;
        state.mistakes += 1; // Penalty for hint
        logEvent('hint', { row: target.r, col: target.c });
        setSelectedCell({ r: target.r, c: target.c });
        checkWin(newGrid);
    }, [state, gridSize, pushHistory, logEvent]);

    const checkWin = (grid: SudokuCell[][]) => {
        if (!activeSession) return;
        let isComplete = true;
        for (let row = 0; row < gridSize; row++) {
            for (let col = 0; col < gridSize; col++) {
                if (grid[row][col].value !== grid[row][col].solution) {
                    isComplete = false;
                    break;
                }
            }
        }
        if (isComplete) {
            completeSession({
                won: true,
                stars: state!.mistakes === 0 ? 3 : state!.mistakes <= 2 ? 2 : 1,
                mistakes: state!.mistakes,
                durationMs: Date.now() - activeSession.startedAt,
                metadata: { difficulty, gridSize }
            });
        }
    };

    const handleInput = useCallback((num: number | null) => {
        if (!state || !selectedCell || !activeSession) return;

        const { r, c } = selectedCell;
        const targetCell = state.grid[r][c];

        if (targetCell.isGiven || targetCell.value === targetCell.solution) return;

        pushHistory(state.grid);
        const newGrid = state.grid.map(row => [...row]);
        const cell = { ...newGrid[r][c] };

        if (isNotesMode && num !== null) {
            // Toggle note
            if (cell.notes.includes(num)) {
                cell.notes = cell.notes.filter(n => n !== num);
            } else {
                cell.notes = [...cell.notes, num];
            }
            newGrid[r][c] = cell;
        } else {
            // Set value
            cell.value = num;
            if (num !== null && num !== targetCell.solution) {
                cell.isError = true;
                logEvent('mistake', { row: r, col: c, input: num, solution: targetCell.solution });
                state.mistakes += 1;
            } else {
                cell.isError = false;
                cell.notes = []; // Clear notes if placed
                if (num !== null) logEvent('correct_input', { row: r, col: c, value: num });
            }
            newGrid[r][c] = cell;
        }

        state.grid = newGrid;
        setSelectedCell({ r, c });

        if (!isNotesMode) checkWin(newGrid);

    }, [state, selectedCell, activeSession, isNotesMode, pushHistory, logEvent]);

    if (!activeSession || !state) return <div className="p-8 text-center animate-pulse">Loading Sudoku...</div>;

    return (
        <div className="flex flex-col gap-6 animate-in fade-in zoom-in duration-500 pb-12">

            {/* Header Info */}
            <div className="flex justify-between items-center bg-panel p-4 rounded-xl shadow-sm border border-black/5 dark:border-white/5">
                <div>
                    <div className="text-sm text-foreground/70 uppercase tracking-wider font-bold">
                        {gridSize}x{gridSize} ΓÇó {difficulty}
                    </div>
                    <div className="text-danger font-bold">
                        Mistakes: {state.mistakes}
                    </div>
                </div>
                <div className="flex gap-2">
                    <Button variant="ghost" size="sm" onClick={handleHint}>≡ƒÆí Hint</Button>
                    <Button variant="ghost" size="sm" onClick={handleUndo} disabled={history.length === 0}>Γå⌐∩╕Å Undo</Button>
                    <Button variant="danger" size="sm" onClick={() => abandonSession()}>Forfeit</Button>
                </div>
            </div>

            <SudokuGrid
                grid={state.grid}
                size={gridSize}
                selectedCell={selectedCell}
                onCellClick={handleCellClick}
            />

            <NumberPad
                size={gridSize}
                onNumberSelect={(num) => handleInput(num)}
                onErase={() => handleInput(null)}
                isNotesMode={isNotesMode}
                onToggleNotes={() => setIsNotesMode(!isNotesMode)}
            />

        </div>
    );
};

'use client';

import React from 'react';
import { SudokuCell } from '@/lib/puzzle/sudoku/types';
import { Icon } from '@/components/ui/Icon';

export const GEM_MAP: Record<number, string> = {
    1: 'gems/gem_1_glowing',
    2: 'gems/gem_2_glowing',
    3: 'gems/gem_3_glowing',
    4: 'gems/gem_4_glowing',
    5: 'gems/gem_5_glowing',
    6: 'gems/gem_6_glowing',
};

interface SudokuGridProps {
    grid: SudokuCell[][];
    size: 4 | 6;
    selectedCell: { r: number; c: number } | null;
    onCellClick: (row: number, col: number) => void;
}

export const SudokuGrid: React.FC<SudokuGridProps> = ({ grid, size, selectedCell, onCellClick }) => {
    // Determine CSS Grid styles based on size
    const gridClass = size === 4 ? 'grid-cols-4' : 'grid-cols-6';
    const blockRows = 2;
    const blockCols = size === 4 ? 2 : 3;

    return (
        <div className={`grid ${gridClass} gap-1 bg-black/20 dark:bg-white/20 p-1 rounded-xl shadow-inner max-w-[400px] mx-auto`}>
            {grid.map((row, rIdx) =>
                row.map((cell, cIdx) => {
                    const isSelected = selectedCell?.r === rIdx && selectedCell?.c === cIdx;

                    // Determine thick borders for subgrids
                    const borderRight = (cIdx + 1) % blockCols === 0 && cIdx !== size - 1 ? 'border-r-2 border-r-black/50 dark:border-r-white/50' : '';
                    const borderBottom = (rIdx + 1) % blockRows === 0 && rIdx !== size - 1 ? 'border-b-2 border-b-black/50 dark:border-b-white/50' : '';

                    // Determine styling states
                    let bgClass = 'bg-panel hover:bg-black/5 dark:hover:bg-white/5';
                    if (isSelected) bgClass = 'bg-primary/20 hover:bg-primary/30';
                    else if (cell.isGiven) bgClass = 'bg-black/5 dark:bg-white/5';

                    let textClass = 'text-foreground font-bold';
                    if (cell.isGiven) textClass = 'text-foreground/70';
                    else if (cell.isError) textClass = 'text-danger font-bold';
                    else textClass = 'text-primary font-bold';

                    return (
                        <button
                            key={`${rIdx}-${cIdx}`}
                            aria-label={`Cell ${rIdx}-${cIdx}`}
                            className={`
                aspect-square flex items-center justify-center text-2xl sm:text-3xl transition-colors relative
                ${bgClass} ${textClass} ${borderRight} ${borderBottom}
                // Rounded corners on the extremes
                ${rIdx === 0 && cIdx === 0 ? 'rounded-tl-lg' : ''}
                ${rIdx === 0 && cIdx === size - 1 ? 'rounded-tr-lg' : ''}
                ${rIdx === size - 1 && cIdx === 0 ? 'rounded-bl-lg' : ''}
                ${rIdx === size - 1 && cIdx === size - 1 ? 'rounded-br-lg' : ''}
              `}
                            onClick={() => onCellClick(rIdx, cIdx)}
                        >
                            {cell.value ? (
                                <div className={`w-3/4 h-3/4 drop-shadow-md flex items-center justify-center ${cell.isError ? 'opacity-50 grayscale' : ''} ${cell.isGiven ? 'opacity-70' : ''}`}>
                                    <Icon assetKey={GEM_MAP[cell.value] as any} />
                                </div>
                            ) : cell.notes.length > 0 ? (
                                <div className="absolute inset-1 grid grid-cols-2 grid-rows-2 sm:grid-cols-3 sm:grid-rows-2 gap-[1px] p-[2px] opacity-70">
                                    {Array.from({ length: size }, (_, i) => i + 1).map(num => (
                                        <div key={num} className="flex items-center justify-center">
                                            {cell.notes.includes(num) && (
                                                <div className="w-full h-full p-[1px] flex items-center justify-center">
                                                    <Icon assetKey={GEM_MAP[num] as any} />
                                                </div>
                                            )}
                                        </div>
                                    ))}
                                </div>
                            ) : null}
                        </button>
                    );
                })
            )}
        </div>
    );
};

'use client';

import React, { useState } from 'react';
import Link from 'next/link';
import { Card } from '@/components/ui/Card';
import { Button } from '@/components/ui/Button';
import { SudokuEngine } from './SudokuEngine';
import { usePuzzleSession } from '@/hooks/usePuzzleSession';

export default function SudokuPage() {
    const { activeSession, abandonSession } = usePuzzleSession('sudoku');

    // Local pre-game configuration state
    const [config, setConfig] = useState<{ size: 4 | 6, diff: 'easy' | 'medium' | 'hard' } | null>(null);

    // If a session is active (or resuming) OR config is chosen, render engine
    if (activeSession?.puzzleId === 'sudoku' || config) {
        const size = (activeSession?.state as any)?.gridSize as 4 | 6 || config?.size || 4;
        const diff = config?.diff || 'easy'; // Fallback if restoring via IDB without local config memory
        return (
            <div className="max-w-3xl mx-auto w-full pt-4">
                <SudokuEngine gridSize={size} difficulty={diff} />
            </div>
        );
    }

    // If a DIFFERENT puzzle types active, lock them out
    if (activeSession) {
        return (
            <div className="max-w-xl mx-auto flex flex-col items-center justify-center min-h-[50vh] text-center p-8">
                <Card variant="panel" className="p-8 border-danger/20 w-full animate-in zoom-in">
                    <h2 className="text-2xl font-bold text-danger mb-4">Active Session Found</h2>
                    <p className="mb-6 text-foreground/80">You already have an active {activeSession.puzzleId} puzzle running. You must finish or forfeit it before starting Sudoku.</p>
