                            <Button variant="primary" className="opacity-80" onClick={() => setConfig({ size: 6, diff: 'medium' })}>Mod</Button>
                            <Button variant="accent" onClick={() => setConfig({ size: 6, diff: 'hard' })}>Hard</Button>
                        </div>
                    </div>

                    <div className="mt-8 pt-8 border-t border-black/5 dark:border-white/5">
                        <Link href="/puzzles" className="contents">
                            <Button variant="ghost" className="w-full">Back to Menu</Button>
                        </Link>
                    </div>
                </div>
            </Card>
        </div>
    );
}

import { useState, useCallback } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { useGameStore } from '@/store/game';
import { PuzzleId, PuzzleSession, PuzzleResult } from '@/lib/puzzle/types';
import { calculateReward, CalculatedReward } from '@/lib/puzzle/rewards';
import { sessionLogger } from '@/lib/puzzle/logger';

export function usePuzzleSession(puzzleId: PuzzleId) {
    const store = useGameStore();
    const activeSession = store.puzzles.activeSession;

    // Local state for the end-of-game modal
    const [showResults, setShowResults] = useState(false);
    const [lastResult, setLastResult] = useState<{ result: PuzzleResult, reward: CalculatedReward } | null>(null);

    /**
     * Starts a new session or resumes if one already exists for this puzzleId.
     */
    const startSession = useCallback((initialState?: Record<string, unknown>) => {
        if (activeSession && activeSession.puzzleId === puzzleId) {
            // Resume existing session
            sessionLogger.log({ eventType: 'resume', sessionId: activeSession.id });
            return activeSession;
        }

        // Create new
        const newSession: PuzzleSession = {
            id: uuidv4(),
            puzzleId,
            seed: Math.random().toString(36).substring(2, 10),
            startedAt: Date.now(),
            state: initialState || null,
        };

        store.setActiveSession(newSession);
        sessionLogger.log({ eventType: 'start', sessionId: newSession.id, details: { seed: newSession.seed } });
        return newSession;
    }, [activeSession, puzzleId, store]);

    const logEvent = useCallback((eventType: any, details?: Record<string, unknown>) => {
        if (activeSession) {
            sessionLogger.log({ eventType, sessionId: activeSession.id, details });
        }
    }, [activeSession]);

    /**
     * Completes the current session, calculates rewards, records completion, and clears active state.
     */
    const completeSession = useCallback((result: PuzzleResult) => {
        if (!activeSession) return;

        sessionLogger.log({ eventType: 'complete', sessionId: activeSession.id, details: { result } });

        // 1. Calculate and distribute rewards
        const reward = calculateReward(activeSession.puzzleId, result);
        store.addCoins(reward.coins);
        store.gainXp(reward.xp);

        // 2. Record completion stats if won
        if (result.won && result.stars > 0) {
            store.recordPuzzleCompletion(activeSession.puzzleId, result.stars, result.durationMs);
        }

        // 3. Clear session
        store.setActiveSession(null);

        // 4. Trigger UI modal
        setLastResult({ result, reward });
        setShowResults(true);
    }, [activeSession, store]);

    /**
     * Abandons the puzzle entirely.
     */
    const abandonSession = useCallback(() => {
        if (!activeSession) return;
        sessionLogger.log({ eventType: 'abandon', sessionId: activeSession.id });
        store.setActiveSession(null);
    }, [activeSession, store]);

    return {
        activeSession,
        startSession,
        completeSession,
        abandonSession,
        logEvent,
        showResults,
        setShowResults,
        lastResult,
    };
}

import { openDB, IDBPDatabase } from 'idb';

const DB_NAME = 'puzzle-pets-save';
const STORE_NAME = 'game-state';
// Bump this version when adding new fields or restructing the save file schema.
const DB_VERSION = 1;

export interface SaveData {
    profile: {
        id: string;
        createdAt: number;
        settings: Record<string, unknown>;
    };
    wallet: {
        coins: number;
    };
    pets: {
        collection: string[]; // array of pet IDs grabbed from manifest
        activePetId: string | null;
        xp: number;
        level: number;
        mood: number;
    };
    puzzles: {
        completedCount: number;
        bestStats: Record<string, { stars: number; time: number }>; // e.g. 'sudoku_4x4' -> { stars: 3, time: 120 }
        activeSession: import('./puzzle/types').PuzzleSession | null;
    };
}

export const INITIAL_SAVE_DATA: SaveData = {
    profile: {
        id: '', // Will be generated on first init
        createdAt: Date.now(),
        settings: {}, // Settings remain in Zustand persist for UI, but backed up here if needed
    },
    wallet: { coins: 0 },
    pets: {
        collection: [],
        activePetId: null,
        xp: 0,
        level: 1,
        mood: 100,
    },
    puzzles: {
        completedCount: 0,
        bestStats: {},
        activeSession: null,
    },
};

/**
 * Initializes and handles migrations for the IndexedDB save file.
 */
async function initDB(): Promise<IDBPDatabase> {
    return openDB(DB_NAME, DB_VERSION, {
        upgrade(db, _oldVersion, _newVersion, _transaction) {
            if (!db.objectStoreNames.contains(STORE_NAME)) {
                db.createObjectStore(STORE_NAME);
            }

            // Future Migrations Example:
            // if (oldVersion < 2) { 
            //    // grab existing data `transaction.objectStore(STORE_NAME).get('save')`
            //    // mutate data
            //    // `transaction.objectStore(STORE_NAME).put(migratedData, 'save')`
            // }
        },
        blocked() {
            console.error('IndexedDB is blocked. Close other tabs of this site!');
        },
        blocking() {
            console.warn('IndexedDB needs to upgrade but is blocked by this tab.');
        }
    });
}

export const saveStorage = {
    async getSave(): Promise<SaveData | null> {
        try {
            const db = await initDB();
            const data = await db.get(STORE_NAME, 'save');
            return data || null;
        } catch (e) {
            console.error('Failed to get save from IndexedDB:', e);
            return null;
        }
    },

    async setSave(data: SaveData): Promise<void> {
        try {
            const db = await initDB();
            await db.put(STORE_NAME, data, 'save');
        } catch (e) {
            console.error('Failed to write save to IndexedDB:', e);
        }
    },

    async clearSave(): Promise<void> {
        try {
            const db = await initDB();
            await db.delete(STORE_NAME, 'save');
        } catch (e) {
            console.error('Failed to clear save in IndexedDB:', e);
        }
    }
};
